import React, { useState, useEffect } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, PieChart, Pie, Cell, LineChart, Line } from 'recharts';
import { User, BookOpen, Play, LogIn, LogOut, Users, TrendingUp, Clock, AlertTriangle } from 'lucide-react';

const StudyProgressTracker = () => {
  const [students, setStudents] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [showNicknameSetup, setShowNicknameSetup] = useState(false);
  const [nickname, setNickname] = useState('');

  // 預設科目和影片數量
  const subjects = [
    { id: 'OS', name: 'OS', totalVideos: 58 },
    { id: '計組', name: '計組', totalVideos: 69 },
    { id: '資結', name: '資結', totalVideos: 67 },
    { id: '演算法', name: '演算法', totalVideos: 16 },
    { id: '線代', name: '線代', totalVideos: 42 },
    { id: '離散', name: '離散', totalVideos: 42 }
  ];

  // 模擬Google登入功能
  const mockGoogleLogin = () => {
    setIsLoading(true);
    // 模擬登入延遲
    setTimeout(() => {
      const mockUser = {
        id: `user_${Date.now()}`,
        googleName: `StudentUser${Math.floor(Math.random() * 100)}@gmail.com`,
        email: `student${Math.floor(Math.random() * 100)}@gmail.com`,
        avatar: `https://ui-avatars.com/api/?name=Student&background=random`
      };
      setCurrentUser(mockUser);
      setShowNicknameSetup(true);
      setIsLoading(false);
    }, 1000);
  };

  // 設定暱稱並完成註冊
  const completeRegistration = () => {
    if (!nickname.trim()) return;
    
    const updatedUser = { ...currentUser, nickname: nickname.trim() };
    setCurrentUser(updatedUser);
    
    // 檢查是否已經有該用戶的進度記錄
    const existingStudent = students.find(s => s.email === updatedUser.email);
    if (!existingStudent) {
      const newStudent = initializeStudentProgress(updatedUser.nickname, updatedUser.email);
      setStudents([...students, newStudent]);
    }
    
    setShowNicknameSetup(false);
    setNickname('');
  };

  // 登出功能
  const logout = () => {
    setCurrentUser(null);
    setShowNicknameSetup(false);
    setNickname('');
  };

  // 初始化學生數據
  const initializeStudentProgress = (name, email) => {
    const progress = {};
    subjects.forEach(subject => {
      progress[subject.id] = 0;
    });
    return { 
      name, 
      email,
      progress, 
      lastUpdated: new Date().toLocaleString('zh-TW', { 
        year: 'numeric', 
        month: '2-digit', 
        day: '2-digit', 
        hour: '2-digit', 
        minute: '2-digit' 
      })
    };
  };

  // 刪除自己的帳戶
  const deleteMyAccount = () => {
    if (currentUser) {
      setStudents(students.filter(s => s.email !== currentUser.email));
      setCurrentUser(null);
    }
  };

  // 更新進度（只能更新自己的）
  const updateProgress = (subjectId, newProgress) => {
    if (!currentUser) return;
    
    const subject = subjects.find(s => s.id === subjectId);
    const clampedProgress = Math.max(0, Math.min(subject.totalVideos, newProgress));
    
    setStudents(students.map(student => 
      student.email === currentUser.email 
        ? { 
            ...student, 
            progress: { ...student.progress, [subjectId]: clampedProgress },
            lastUpdated: new Date().toLocaleString('zh-TW', { 
              year: 'numeric', 
              month: '2-digit', 
              day: '2-digit', 
              hour: '2-digit', 
              minute: '2-digit' 
            })
          }
        : student
    ));
  };

  // 準備圖表數據
  const getChartData = () => {
    return subjects.map(subject => {
      const data = { subject: subject.name, total: subject.totalVideos };
      students.forEach(student => {
        data[student.name] = student.progress[subject.id];
      });
      return data;
    });
  };

  // 計算總體進度
  const getTotalProgress = (student) => {
    const totalVideos = subjects.reduce((sum, subject) => sum + subject.totalVideos, 0);
    const watchedVideos = subjects.reduce((sum, subject) => sum + student.progress[subject.id], 0);
    return Math.round((watchedVideos / totalVideos) * 100);
  };

  // 獲取當前用戶的進度數據
  const getCurrentUserProgress = () => {
    if (!currentUser) return null;
    return students.find(s => s.email === currentUser.email);
  };

  const colors = ['#8884d8', '#82ca9d', '#ffc658', '#ff7300', '#8dd1e1', '#d084d0'];

  // 未登入狀態或設定暱稱狀態
  if (!currentUser || showNicknameSetup) {
    return (
      <div className="max-w-4xl mx-auto p-6 bg-gray-50 min-h-screen flex items-center justify-center">
        <div className="bg-white rounded-lg shadow-lg p-8 text-center max-w-md w-full">
          <BookOpen className="mx-auto text-blue-600 mb-4" size={64} />
          
          {!currentUser ? (
            <>
              <h1 className="text-2xl font-bold text-gray-800 mb-2">學習進度追蹤系統</h1>
              <p className="text-gray-600 mb-6">請先登入 Google 帳戶來追蹤你的學習進度</p>
              
              <button
                onClick={mockGoogleLogin}
                disabled={isLoading}
                className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white font-medium py-3 px-4 rounded-lg flex items-center justify-center gap-2 transition-colors"
              >
                {isLoading ? (
                  <>
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
                    登入中...
                  </>
                ) : (
                  <>
                    <LogIn size={20} />
                    使用 Google 帳戶登入
                  </>
                )}
              </button>
            </>
          ) : (
            <>
              <img 
                src={currentUser.avatar} 
                alt="用戶頭像" 
                className="w-16 h-16 rounded-full mx-auto mb-4"
              />
              <h1 className="text-2xl font-bold text-gray-800 mb-2">設定你的暱稱</h1>
              <p className="text-gray-600 mb-4">為了更好的顯示效果，請設定一個簡短的暱稱</p>
              <p className="text-sm text-gray-500 mb-4">Google 帳戶：{currentUser.email}</p>
              
              <input
                type="text"
                value={nickname}
                onChange={(e) => setNickname(e.target.value)}
                placeholder="輸入你的暱稱（例如：小明、Amy）"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
                onKeyPress={(e) => e.key === 'Enter' && completeRegistration()}
                maxLength={20}
              />
              
              <button
                onClick={completeRegistration}
                disabled={!nickname.trim()}
                className="w-full bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 text-white font-medium py-2 px-4 rounded-lg transition-colors"
              >
                開始使用
              </button>
            </>
          )}
          
          <div className="mt-4 text-sm text-gray-500">
            <p>* 這是模擬登入，實際部署時需要串接真實的 Google OAuth</p>
          </div>
        </div>
      </div>
    );
  }

  const currentUserProgress = getCurrentUserProgress();

  return (
    <div className="max-w-7xl mx-auto p-6 bg-gray-50 min-h-screen">
      {/* 頂部用戶資訊 */}
      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <img 
              src={currentUser.avatar} 
              alt="用戶頭像" 
              className="w-12 h-12 rounded-full"
            />
            <div>
              <h1 className="text-2xl font-bold text-gray-800 flex items-center gap-2">
                <BookOpen className="text-blue-600" />
                歡迎回來，{currentUser.nickname}！
              </h1>
              <p className="text-gray-600">{currentUser.email}</p>
            </div>
          </div>
          
          <div className="flex items-center gap-2">
            <button
              onClick={deleteMyAccount}
              className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 flex items-center gap-2"
            >
              <AlertTriangle size={16} />
              刪除我的帳戶
            </button>
            <button
              onClick={logout}
              className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 flex items-center gap-2"
            >
              <LogOut size={16} />
              登出
            </button>
          </div>
        </div>
      </div>

      {currentUserProgress && (
        <>
          {/* 我的進度更新區 */}
          <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
            <h2 className="text-xl font-semibold mb-4 flex items-center gap-2">
              <Play className="text-purple-600" />
              更新我的進度
            </h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {subjects.map(subject => (
                <div key={subject.id} className="border border-gray-200 rounded-lg p-4">
                  <h3 className="font-medium text-gray-800 mb-3">{subject.name}</h3>
                  <p className="text-sm text-gray-500 mb-3">總共 {subject.totalVideos} 部影片</p>
                  <div className="mb-2">
                    <label className="block text-sm font-medium mb-1 text-blue-600">
                      我的進度
                    </label>
                    <div className="flex items-center gap-2">
                      <input
                        type="number"
                        min="0"
                        max={subject.totalVideos}
                        value={currentUserProgress.progress[subject.id]}
                        onChange={(e) => updateProgress(subject.id, parseInt(e.target.value) || 0)}
                        className="w-20 px-2 py-1 border border-gray-300 rounded text-sm focus:outline-none focus:ring-1 focus:ring-blue-500"
                      />
                      <span className="text-sm text-gray-500">
                        / {subject.totalVideos} ({Math.round((currentUserProgress.progress[subject.id] / subject.totalVideos) * 100)}%)
                      </span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2 mt-1">
                      <div
                        className="h-2 rounded-full transition-all duration-300 bg-blue-600"
                        style={{
                          width: `${(currentUserProgress.progress[subject.id] / subject.totalVideos) * 100}%`
                        }}
                      />
                    </div>
                  </div>
                </div>
              ))}
            </div>
            
            {currentUserProgress.lastUpdated && (
              <div className="mt-4 flex items-center gap-2 text-sm text-gray-500">
                <Clock size={16} />
                最後更新時間：{currentUserProgress.lastUpdated}
              </div>
            )}
          </div>
        </>
      )}

      {students.length > 0 && (
        <>
          {/* 圖表區域 */}
          <div className="grid grid-cols-1 gap-6 mb-6">
            {/* 長條圖 */}
            <div className="bg-white rounded-lg shadow-lg p-6">
              <h2 className="text-xl font-semibold mb-4 flex items-center gap-2">
                <TrendingUp className="text-blue-600" />
                各科目進度比較
              </h2>
              <BarChart width={800} height={300} data={getChartData()}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="subject" />
                <YAxis />
                <Tooltip />
                <Legend />
                {students.map((student, index) => (
                  <Bar
                    key={student.name}
                    dataKey={student.name}
                    fill={colors[index % colors.length]}
                  />
                ))}
              </BarChart>
            </div>

            {/* 圓餅圖區域 - 每個學生都有自己的圓餅圖 */}
            <div className="col-span-1 lg:col-span-2">
              <div className="bg-white rounded-lg shadow-lg p-6">
                <h2 className="text-xl font-semibold mb-4">各學生進度分布</h2>
                <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                  {students.map((student, studentIndex) => (
                    <div key={student.email} className="border border-gray-200 rounded-lg p-4">
                      <h3 className="font-medium mb-2 flex items-center gap-2">
                        <User size={16} style={{ color: colors[studentIndex % colors.length] }} />
                        {student.name} 的進度分布
                        {student.email === currentUser.email && (
                          <span className="text-xs bg-blue-100 text-blue-600 px-2 py-1 rounded-full">我</span>
                        )}
                      </h3>
                      <div className="flex items-center gap-2 text-xs text-gray-500 mb-2">
                        <Clock size={12} />
                        最後更新：{student.lastUpdated}
                      </div>
                      <PieChart width={300} height={200}>
                        <Pie
                          data={subjects.map(subject => ({
                            name: subject.name,
                            value: student.progress[subject.id],
                            total: subject.totalVideos
                          }))}
                          cx={150}
                          cy={100}
                          labelLine={false}
                          label={({ name, value, total }) => `${name}: ${value}/${total}`}
                          outerRadius={60}
                          fill="#8884d8"
                          dataKey="value"
                        >
                          {subjects.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={colors[index % colors.length]} />
                          ))}
                        </Pie>
                        <Tooltip />
                      </PieChart>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>

          {/* 總進度概覽 */}
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-xl font-semibold mb-4">總進度概覽</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {students.map((student, index) => {
                const totalProgress = getTotalProgress(student);
                const totalWatched = subjects.reduce((sum, subject) => sum + student.progress[subject.id], 0);
                const totalVideos = subjects.reduce((sum, subject) => sum + subject.totalVideos, 0);
                
                return (
                  <div key={student.email} className="border border-gray-200 rounded-lg p-4">
                    <div className="flex items-center gap-2 mb-2">
                      <User size={20} style={{ color: colors[index % colors.length] }} />
                      <h3 className="font-medium">{student.name}</h3>
                      {student.email === currentUser.email && (
                        <span className="text-xs bg-blue-100 text-blue-600 px-2 py-1 rounded-full">我</span>
                      )}
                    </div>
                    <p className="text-2xl font-bold mb-1" style={{ color: colors[index % colors.length] }}>
                      {totalProgress}%
                    </p>
                    <p className="text-sm text-gray-500 mb-2">
                      {totalWatched} / {totalVideos} 部影片
                    </p>
                    <div className="flex items-center gap-2 text-xs text-gray-400 mb-2">
                      <Clock size={12} />
                      最後更新：{student.lastUpdated}
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-3">
                      <div
                        className="h-3 rounded-full transition-all duration-300"
                        style={{
                          width: `${totalProgress}%`,
                          backgroundColor: colors[index % colors.length]
                        }}
                      />
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default StudyProgressTracker;
